import React, { useState } from 'react';
import './ChatGPT.css';

function ChatGPT() {
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const API_KEY = process.env.REACT_APP_OPENAI_API_KEY;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    if (!API_KEY) {
      setMessages(prev => [...prev, { type: 'error', content: 'Error: API key is not configured properly' }]);
      return;
    }

    // Add user message immediately
    setMessages(prev => [...prev, { type: 'user', content: question }]);
    setIsLoading(true);

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${API_KEY}`
        },
        body: JSON.stringify({
          model: "gpt-4-turbo-preview",
          messages: [
            {
              role: "user",
              content: question
            }
          ],
          max_tokens: 1000,
          temperature: 0.7
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error?.message || 'API request failed');
      }

      const data = await response.json();
      if (data.choices && data.choices[0]) {
        setMessages(prev => [...prev, { type: 'assistant', content: data.choices[0].message.content }]);
      } else {
        throw new Error('No response from ChatGPT');
      }
    } catch (error) {
      console.error('Error details:', error);
      setMessages(prev => [...prev, { type: 'error', content: `Error: ${error.message}` }]);
    } finally {
      setIsLoading(false);
      setQuestion('');
    }
  };

  return (
    <div className="chatgpt-container">
      <div className="chat-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="message-content">
              {message.type === 'user' && <span className="avatar user">You</span>}
              {message.type === 'assistant' && <span className="avatar assistant">AI</span>}
              <p>{message.content}</p>
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="message assistant">
            <div className="message-content">
              <span className="avatar assistant">AI</span>
              <p>Thinking...</p>
            </div>
          </div>
        )}
      </div>
      <form onSubmit={handleSubmit} className="chat-input">
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Type your message here..."
          disabled={isLoading}
          required
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSubmit(e);
            }
          }}
        />
        <button type="submit" disabled={isLoading}>
          Send
        </button>
      </form>
    </div>
  );
}

export default ChatGPT;
