import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './WalletCard.css';

function WalletCard({ account, balance, network }) {
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [nfts, setNfts] = useState([]);

  useEffect(() => {
    const fetchNFTs = async () => {
      // Add NFT fetching logic here using Alchemy or similar API
      // This is a placeholder
      const response = await fetch(
        `https://api.opensea.io/api/v1/assets?owner=${account.address}&limit=20`
      );
      const data = await response.json();
      setNfts(data.assets);
    };

    if (account) {
      fetchNFTs();
    }
  }, [account]);

  const sendTransaction = async () => {
    try {
      const tx = await account.sendTransaction({
        to: recipient,
        value: ethers.utils.parseEther(amount)
      });
      await tx.wait();
      alert('Transaction successful!');
    } catch (error) {
      alert('Transaction failed: ' + error.message);
    }
  };

  return (
    <div className="wallet-card">
      <div className="balance-section">
        <h2>Balance</h2>
        <p className="balance">{balance} ETH</p>
        <p className="network">{network}</p>
      </div>

      <div className="transfer-section">
        <h3>Send ETH</h3>
        <input
          type="text"
          placeholder="Recipient Address"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
        <input
          type="number"
          placeholder="Amount in ETH"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <button onClick={sendTransaction}>Send</button>
      </div>

      <div className="nft-section">
        <h3>NFTs</h3>
        <div className="nft-grid">
          {nfts.map((nft) => (
            <div key={nft.id} className="nft-item">
              <img src={nft.image_url} alt={nft.name} />
              <p>{nft.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default WalletCard;
