import React from 'react';
import './OnboardingSteps.css';

function OnboardingSteps({ onComplete }) {
  const [currentStep, setCurrentStep] = React.useState(0);

  const steps = [
    {
      title: "Understanding Cryptocurrency Wallets",
      content: [
        "A cryptocurrency wallet is a digital tool that allows you to:",
        "• Store your cryptocurrencies securely",
        "• Send and receive digital assets",
        "• Interact with blockchain applications",
        "• Manage your crypto portfolio"
      ]
    },
    {
      title: "Security Best Practices",
      content: [
        "Keep your wallet safe by following these guidelines:",
        "• Never share your private key or seed phrase",
        "• Store your backup phrase offline",
        "• Use strong passwords",
        "• Be cautious of phishing attempts"
      ]
    },
    {
      title: "Making Transactions",
      content: [
        "When sending or receiving crypto:",
        "• Double-check all addresses",
        "• Verify transaction fees",
        "• Start with small amounts",
        "• Confirm network compatibility"
      ]
    }
  ];

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      onComplete();
    }
  };

  return (
    <div className="onboarding-steps">
      <div className="onboarding-content">
        <h2>{steps[currentStep].title}</h2>
        <div className="step-content">
          {steps[currentStep].content.map((line, index) => (
            <p key={index}>{line}</p>
          ))}
        </div>
        <div className="step-navigation">
          <div className="step-indicators">
            {steps.map((_, index) => (
              <div
                key={index}
                className={`step-dot ${index === currentStep ? 'active' : ''}`}
              />
            ))}
          </div>
          <div className="step-buttons">
            <button 
              onClick={onComplete} 
              className="secondary-button"
            >
              Skip Tutorial
            </button>
            <button 
              onClick={handleNext}
              className="primary-button"
            >
              {currentStep === steps.length - 1 ? 'Finish' : 'Next'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OnboardingSteps;
