function DAOInterface({ account, network }) {
  const [contract, setContract] = useState(null);
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newProposal, setNewProposal] = useState('');

  useEffect(() => {
    const initializeContract = async () => {
      try {
        setLoading(true);
        setError(null);

        if (network === 'mainnet') {
          setError('DAO functionality is only available on Sepolia testnet');
          setLoading(false);
          return;
        }

        const contractAddress = process.env.REACT_APP_DAO_CONTRACT_SEPOLIA;

        if (!contractAddress) {
          throw new Error('No DAO contract address configured for Sepolia network');
        }

        const daoContract = new ethers.Contract(
          contractAddress,
          daoABI,
          account.provider
        );

        setContract(daoContract);

        // Fetch actual proposals from the contract
        const proposalCount = await daoContract.proposalCount();
        const fetchedProposals = [];

        for (let i = 1; i <= proposalCount; i++) {
          const proposal = await daoContract.getProposal(i);
          fetchedProposals.push({
            id: i,
            proposer: proposal.proposer,
            description: proposal.description,
            forVotes: proposal.yesVotes.toString(),
            againstVotes: proposal.noVotes.toString(),
            executed: proposal.executed,
            endTime: proposal.endTime.toNumber()
          });
        }

        setProposals(fetchedProposals);
      } catch (err) {
        console.error('Error initializing DAO contract:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (account) {
      initializeContract();
    }
  }, [account, network]);

  const handleVote = async (proposalId, support) => {
    if (network === 'mainnet') {
      alert('DAO functionality is only available on Sepolia testnet');
      return;
    }

    try {
      const tx = await contract.vote(proposalId, support);
      await tx.wait();
      alert('Vote submitted successfully!');
      // Refresh proposals
      const proposal = await contract.getProposal(proposalId);
      setProposals(proposals.map(p => 
        p.id === proposalId 
          ? {
              ...p,
              forVotes: proposal.yesVotes.toString(),
              againstVotes: proposal.noVotes.toString()
            }
          : p
      ));
    } catch (error) {
      alert('Error voting: ' + error.message);
    }
  };

  const handleCreateProposal = async (e) => {
    e.preventDefault();
    if (network === 'mainnet') {
      alert('DAO functionality is only available on Sepolia testnet');
      return;
    }

    try {
      const tx = await contract.createProposal(newProposal);
      await tx.wait();
      alert('Proposal created successfully!');
      setNewProposal('');

      // Refresh proposals
      const proposalCount = await contract.proposalCount();
      const newProposalData = await contract.getProposal(proposalCount);
      setProposals([...proposals, {
        id: proposalCount,
        proposer: newProposalData.proposer,
        description: newProposalData.description,
        forVotes: newProposalData.yesVotes.toString(),
        againstVotes: newProposalData.noVotes.toString(),
        executed: newProposalData.executed,
        endTime: newProposalData.endTime.toNumber()
      }]);
    } catch (error) {
      alert('Error creating proposal: ' + error.message);
    }
  };

  if (network === 'mainnet') {
    return (
      <div className="dao-error">
        <h2>Network Not Supported</h2>
        <p>DAO functionality is only available on Sepolia testnet.</p>
        <p>Please switch to Sepolia network to access DAO features.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="dao-loading">
        <LoadingSpinner />
        <p>Loading DAO interface...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dao-error">
        <h2>Error Loading DAO</h2>
        <p>{error}</p>
        <p>Please make sure you're connected to the correct network and try again.</p>
      </div>
    );
  }

  return (
    <div className="dao-interface">
      <h2>DAO Governance</h2>

      <div className="create-proposal">
        <h3>Create Proposal</h3>
        <form onSubmit={handleCreateProposal}>
          <textarea
            value={newProposal}
            onChange={(e) => setNewProposal(e.target.value)}
            placeholder="Enter your proposal description"
            required
          />
          <button type="submit">Submit Proposal</button>
        </form>
      </div>

      <div className="proposals-list">
        <h3>Active Proposals</h3>
        {proposals.map((proposal) => (
          <div key={proposal.id} className="proposal-item">
            <h4>Proposal #{proposal.id}</h4>
            <p className="proposal-description">{proposal.description}</p>
            <div className="vote-stats">
              <span>For: {proposal.forVotes}</span>
              <span>Against: {proposal.againstVotes}</span>
              {proposal.executed && <span className="executed">Executed</span>}
            </div>
            {!proposal.executed && (
              <div className="vote-buttons">
                <button 
                  onClick={() => handleVote(proposal.id, true)}
                  className="vote-for"
                >
                  Vote For
                </button>
                <button 
                  onClick={() => handleVote(proposal.id, false)}
                  className="vote-against"
                >
                  Vote Against
                </button>
              </div>
            )}
          </div>
        ))}
        {proposals.length === 0 && (
          <p className="no-proposals">No active proposals</p>
        )}
      </div>
    </div>
  );
}

export default DAOInterface;
