import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';
import Navbar from './components/Navbar';
import WalletCard from './components/WalletCard';
import TransactionHistory from './components/TransactionHistory';
import DAOInterface from './components/DAOInterface';

function App() {
  const [account, setAccount] = useState(null);
  const [network, setNetwork] = useState('mainnet');
  const [balance, setBalance] = useState(0);
  const [view, setView] = useState('welcome');
  const [isLoading, setIsLoading] = useState(true);
  const [seedPhrase, setSeedPhrase] = useState(null);

  const getProvider = (networkName) => {
    try {
      const rpcUrl = networkName === 'mainnet'
        ? process.env.REACT_APP_MAINNET_RPC
        : process.env.REACT_APP_SEPOLIA_RPC;

      if (!rpcUrl) {
        throw new Error(`No RPC URL configured for ${networkName}`);
      }

      return new ethers.providers.JsonRpcProvider(
        rpcUrl,
        networkName === 'mainnet' ? 'mainnet' : 'sepolia'
      );
    } catch (error) {
      console.error('Error creating provider:', error);
      return null;
    }
  };

  useEffect(() => {
    // Clear effect to prevent automatic loading
    localStorage.removeItem('wallet');
    setIsLoading(false);
  }, []);

  useEffect(() => {
    console.log('Current view:', view);
  }, [view]);

  useEffect(() => {
    const updateWalletForNetwork = async () => {
      if (account) {
        try {
          const newProvider = getProvider(network);
          if (!newProvider) {
            throw new Error('Failed to create provider');
          }

          // Wait for provider to be ready
          await newProvider.ready;

          const wallet = account.connect(newProvider);
          setAccount(wallet);

          const balance = await newProvider.getBalance(wallet.address);
          setBalance(Number(ethers.utils.formatEther(balance)).toFixed(5));
        } catch (error) {
          console.error('Error updating wallet for network:', error);
          // Optionally revert to previous network
          // setNetwork(previousNetwork);
        }
      }
    };

    updateWalletForNetwork();
  }, [network, account]);

  const loadWallet = async (walletData) => {
    try {
      const provider = getProvider(network);
      if (!provider) {
        throw new Error('Failed to create provider');
      }

      await provider.ready; // Wait for provider to be ready

      const wallet = new ethers.Wallet(walletData.privateKey, provider);
      setAccount(wallet);
      const balance = await provider.getBalance(wallet.address);
      setBalance(Number(ethers.utils.formatEther(balance)).toFixed(5));
      setView('wallet');
    } catch (error) {
      console.error('Error loading wallet:', error);
      alert('Failed to load wallet');
      localStorage.removeItem('wallet');
      setView('welcome');
    }
  };

  const createWallet = async () => {
    try {
      const provider = getProvider(network);
      if (!provider) {
        throw new Error('Failed to create provider');
      }

      await provider.ready; // Wait for provider to be ready

      const wallet = ethers.Wallet.createRandom();
      const walletData = {
        address: wallet.address,
        privateKey: wallet.privateKey,
        mnemonic: wallet.mnemonic.phrase
      };
      const connectedWallet = wallet.connect(provider);
      setAccount(connectedWallet);
      setSeedPhrase(wallet.mnemonic.phrase);
      setView('seed-phrase');
    } catch (error) {
      console.error('Error creating wallet:', error);
      alert('Failed to create wallet');
    }
  };

  const confirmSeedPhrase = () => {
    const walletData = {
      address: account.address,
      privateKey: account.privateKey,
      mnemonic: seedPhrase
    };
    localStorage.setItem('wallet', JSON.stringify(walletData));
    setSeedPhrase(null);
    setView('wallet');
  };

  const importWallet = (seedPhrase) => {
    try {
      if (!seedPhrase || seedPhrase.trim() === '') {
        throw new Error('Seed phrase is required');
      }

      // Validate seed phrase format (12 or 24 words)
      const words = seedPhrase.trim().split(' ');
      if (words.length !== 12 && words.length !== 24) {
        throw new Error('Seed phrase must be 12 or 24 words');
      }

      // Create wallet from mnemonic
      const wallet = ethers.Wallet.fromMnemonic(seedPhrase.trim());
      const connectedWallet = wallet.connect(provider);

      const walletData = {
        address: wallet.address,
        privateKey: wallet.privateKey,
        mnemonic: seedPhrase
      };

      localStorage.setItem('wallet', JSON.stringify(walletData));
      setAccount(connectedWallet);
      setView('wallet');
    } catch (error) {
      console.error('Invalid seed phrase:', error);
      alert('Invalid seed phrase. Please check and try again.');
    }
  };

  const logout = () => {
    localStorage.removeItem('wallet');
    setAccount(null);
    setBalance(0);
    setView('welcome');
  };

  const renderContent = () => {
    if (isLoading) {
      return <div className="loading">Loading...</div>;
    }

    if (view === 'import') {
      return (
        <div className="import-screen">
          <h2>Import Wallet</h2>
          <p className="import-instruction">
            Enter your 12 or 24-word seed phrase to recover your wallet
          </p>
          <form onSubmit={(e) => {
            e.preventDefault();
            const seedPhrase = e.target.seedPhrase.value;
            importWallet(seedPhrase);
          }}>
            <textarea
              name="seedPhrase"
              placeholder="Enter your seed phrase (each word separated by a space)"
              required
              className="seed-phrase-input"
              rows="4"
            />
            <div className="import-actions">
              <button type="submit">Import Wallet</button>
              <button 
                type="button"
                onClick={() => setView('welcome')}
                className="secondary-button"
              >
                Back
              </button>
            </div>
          </form>
        </div>
      );
    }

    if (view === 'seed-phrase' && seedPhrase) {
      return (
        <div className="seed-phrase-screen">
          <h2>Save Your Seed Phrase</h2>
          <p className="warning">
            Write down these 12 words in a secure location. You'll need them to recover your wallet.
          </p>
          <div className="seed-phrase-container">
            {seedPhrase.split(' ').map((word, index) => (
              <div key={index} className="seed-word">
                <span className="word-number">{index + 1}.</span>
                <span className="word">{word}</span>
              </div>
            ))}
          </div>
          <div className="seed-phrase-actions">
            <p className="confirmation-text">
              I have safely stored my seed phrase
            </p>
            <button onClick={confirmSeedPhrase}>Continue to Wallet</button>
            <button 
              onClick={() => {
                setAccount(null);
                setSeedPhrase(null);
                setView('welcome');
              }}
              className="secondary-button"
            >
              Cancel
            </button>
          </div>
        </div>
      );
    }

    if (!account || view === 'welcome') {
      return (
        <div className="welcome-screen">
          <h1>Welcome to Crypto Wallet</h1>
          <div className="welcome-buttons">
            <button onClick={createWallet}>Create New Wallet</button>
            <button onClick={() => {
              console.log('Setting view to import');
              setView('import');
            }}>Import Existing Wallet</button>
          </div>
        </div>
      );
    }

    switch (view) {
      case 'wallet':
        return (
          <WalletCard 
            account={account}
            balance={balance}
            network={network}
          />
        );
      case 'transactions':
        return (
          <TransactionHistory 
            account={account}
            network={network}
          />
        );
      case 'dao':
        return (
          <DAOInterface 
            account={account}
            network={network}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="app">
      <Navbar 
        account={account} 
        network={network}
        setNetwork={setNetwork}
        setView={setView}
        onLogout={logout}
        view={view}
      />
      <main className="main-content">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
