function DAOInterface({ account, network }) {
  const [contract, setContract] = useState(null);
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newProposal, setNewProposal] = useState('');
  const [notification, setNotification] = useState(null);

  // Add notification timeout cleanup
  useEffect(() => {
    if (notification) {
      const timer = setTimeout(() => {
        setNotification(null);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [notification]);

  useEffect(() => {
    const initializeContract = async () => {
      try {
        setLoading(true);
        setError(null);

        // For demo purposes, using a sample contract address
        // Replace with your actual contract addresses
        const contractAddress = network === 'mainnet'
          ? '0x123...your-mainnet-contract' // Replace with actual address
          : '0xfEd0dA98cBb87a7035F52dbF85116f01Db020042'; // Replace with actual address

        if (!contractAddress) {
          throw new Error(`No DAO contract address configured for ${network} network`);
        }

        const daoContract = new ethers.Contract(
          contractAddress,
          daoABI,
          account.provider
        );

        setContract(daoContract);

        // For demo purposes, creating sample proposals
        const sampleProposals = [
          {
            id: 1,
            description: "Implement new staking mechanism",
            forVotes: "100",
            againstVotes: "20",
            executed: false,
            canceled: false
          },
          {
            id: 2,
            description: "Add support for new token standard",
            forVotes: "150",
            againstVotes: "50",
            executed: true,
            canceled: false
          }
        ];

        setProposals(sampleProposals);
      } catch (err) {
        console.error('Error initializing DAO contract:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (account) {
      initializeContract();
    }
  }, [account, network]);

  const handleVote = async (proposalId, support) => {
    try {
      // In a real implementation, this would call the contract's vote function
      setNotification({
        type: 'success',
        message: `Voted ${support ? 'for' : 'against'} proposal ${proposalId}`
      });
    } catch (error) {
      setNotification({
        type: 'error',
        message: 'Error voting: ' + error.message
      });
    }
  };

  const handleCreateProposal = async (e) => {
    e.preventDefault();
    try {
      // In a real implementation, this would call the contract's createProposal function
      setNotification({
        type: 'success',
        message: `Created proposal: ${newProposal}`
      });
      setNewProposal('');
    } catch (error) {
      setNotification({
        type: 'error',
        message: 'Error creating proposal: ' + error.message
      });
    }
  };

  if (loading) {
    return (
      <div className="dao-loading">
        <LoadingSpinner />
        <p>Loading DAO interface...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dao-error">
        <h2>Error Loading DAO</h2>
        <p>{error}</p>
        <p>Please make sure you're connected to the correct network and try again.</p>
      </div>
    );
  }

  return (
    <div className="dao-interface">
      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      <h2>DAO Governance</h2>

      <div className="create-proposal">
        <h3>Create Proposal</h3>
        <form onSubmit={handleCreateProposal}>
          <textarea
            value={newProposal}
            onChange={(e) => setNewProposal(e.target.value)}
            placeholder="Enter your proposal description"
            required
          />
          <button type="submit">Submit Proposal</button>
        </form>
      </div>

      <div className="proposals-list">
        <h3>Active Proposals</h3>
        {proposals.map((proposal) => (
          <div key={proposal.id} className="proposal-item">
            <h4>Proposal #{proposal.id}</h4>
            <p className="proposal-description">{proposal.description}</p>
            <div className="vote-stats">
              <span>For: {proposal.forVotes}</span>
              <span>Against: {proposal.againstVotes}</span>
              {proposal.executed && <span className="executed">Executed</span>}
              {proposal.canceled && <span className="canceled">Canceled</span>}
            </div>
            {!proposal.executed && !proposal.canceled && (
              <div className="vote-buttons">
                <button 
                  onClick={() => handleVote(proposal.id, true)}
                  className="vote-for"
                >
                  Vote For
                </button>
                <button 
                  onClick={() => handleVote(proposal.id, false)}
                  className="vote-against"
                >
                  Vote Against
                </button>
              </div>
            )}
          </div>
        ))}
        {proposals.length === 0 && (
          <p className="no-proposals">No active proposals</p>
        )}
      </div>
    </div>
  );
}

export default DAOInterface;
