function DAOInterface({ account, network }) {
  const [contract, setContract] = useState(null);
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newProposal, setNewProposal] = useState('');
  const [isMember, setIsMember] = useState(false);

  const fetchProposals = async (daoContract) => {
    try {
      console.log('Fetching proposal count...');
      const count = await daoContract.proposalCount();
      const proposalCount = count.toNumber();
      console.log('Proposal count:', proposalCount);

      const fetchedProposals = [];

      for (let i = 0; i < proposalCount; i++) {
        try {
          console.log('Fetching proposal:', i);
          const proposal = await daoContract.proposals(i);
          fetchedProposals.push({
            id: i,
            proposer: proposal.proposer,
            description: proposal.description,
            forVotes: proposal.yesVotes.toString(),
            againstVotes: proposal.noVotes.toString(),
            endTime: proposal.endTime.toNumber(),
            executed: proposal.executed
          });
        } catch (proposalError) {
          console.error(`Error fetching proposal ${i}:`, proposalError);
        }
      }

      console.log('Fetched proposals:', fetchedProposals);
      setProposals(fetchedProposals.reverse()); // Show newest first
    } catch (error) {
      console.error('Error in fetchProposals:', error);
      if (error.code === 'CALL_EXCEPTION') {
        setError('Contract interaction failed. Please check if you are connected to the correct network.');
      } else {
        setError(error.message || 'Failed to fetch proposals');
      }
    }
  };

  useEffect(() => {
    const initializeContract = async () => {
      try {
        setLoading(true);
        setError(null);

        const contractAddress = network === 'mainnet'
          ? process.env.REACT_APP_DAO_CONTRACT_MAINNET
          : process.env.REACT_APP_DAO_CONTRACT_SEPOLIA;

        if (!contractAddress) {
          throw new Error(`No DAO contract address configured for ${network} network`);
        }

        console.log('Initializing contract with address:', contractAddress);

        // First try with provider
        const provider = account.provider;
        const daoContract = new ethers.Contract(
          contractAddress,
          daoABI,
          provider
        );

        setContract(daoContract);

        // Check if user is a member
        try {
          const memberRole = await daoContract.MEMBER_ROLE();
          const memberStatus = await daoContract.hasRole(memberRole, account.address);
          setIsMember(memberStatus);
        } catch (memberError) {
          console.error('Error checking membership:', memberError);
          setIsMember(false);
        }

        await fetchProposals(daoContract);
      } catch (err) {
        console.error('Error initializing DAO contract:', err);
        if (err.code === 'INVALID_ARGUMENT') {
          setError('Invalid contract address. Please check your configuration.');
        } else if (err.code === 'CALL_EXCEPTION') {
          setError('Contract interaction failed. Please check if you are connected to the correct network.');
        } else {
          setError(err.message);
        }
      } finally {
        setLoading(false);
      }
    };

    if (account) {
      initializeContract();
    }
  }, [account, network]);

  const handleJoinDAO = async () => {
    try {
      const signer = account.provider.getSigner();
      const connectedContract = contract.connect(signer);

      const tx = await connectedContract.joinDAO();
      await tx.wait();

      // Update membership status
      const memberRole = await contract.MEMBER_ROLE();
      const memberStatus = await contract.hasRole(memberRole, account.address);
      setIsMember(memberStatus);

      alert('Successfully joined the DAO!');
    } catch (error) {
      alert('Error joining DAO: ' + error.message);
    }
  };

  const handleVote = async (proposalId, support) => {
    try {
      const signer = account.provider.getSigner();
      const connectedContract = contract.connect(signer);

      const tx = await connectedContract.vote(proposalId, support);
      await tx.wait();

      // Refresh proposals after voting
      await fetchProposals(contract);
      alert('Vote submitted successfully!');
    } catch (error) {
      alert('Error voting: ' + error.message);
    }
  };

  const handleCreateProposal = async (e) => {
    e.preventDefault();
    try {
      const signer = account.provider.getSigner();
      const connectedContract = contract.connect(signer);

      const tx = await connectedContract.createProposal(newProposal);
      await tx.wait();

      // Refresh proposals after creating new one
      await fetchProposals(contract);
      setNewProposal('');
      alert('Proposal created successfully!');
    } catch (error) {
      alert('Error creating proposal: ' + error.message);
    }
  };

  if (loading) {
    return (
      <div className="dao-loading">
        <LoadingSpinner />
        <p>Loading DAO interface...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dao-error">
        <h2>Error Loading DAO</h2>
        <p>{error}</p>
        <p>Please make sure you're connected to the correct network and try again.</p>
      </div>
    );
  }

  return (
    <div className="dao-interface">
      <h2>DAO Governance</h2>

      {!isMember ? (
        <div className="join-dao">
          <p>You need to be a member to participate in the DAO</p>
          <button onClick={handleJoinDAO}>Join DAO</button>
        </div>
      ) : (
        <>
          <div className="create-proposal">
            <h3>Create Proposal</h3>
            <form onSubmit={handleCreateProposal}>
              <textarea
                value={newProposal}
                onChange={(e) => setNewProposal(e.target.value)}
                placeholder="Enter your proposal description"
                required
              />
              <button type="submit">Submit Proposal</button>
            </form>
          </div>

          <div className="proposals-list">
            <h3>Active Proposals</h3>
            {proposals.map((proposal) => (
              <div key={proposal.id} className="proposal-item">
                <h4>Proposal #{proposal.id}</h4>
                <p className="proposal-description">{proposal.description}</p>
                <p className="proposal-proposer">
                  Proposer: {proposal.proposer.slice(0, 6)}...{proposal.proposer.slice(-4)}
                </p>
                <div className="vote-stats">
                  <span>For: {proposal.forVotes}</span>
                  <span>Against: {proposal.againstVotes}</span>
                  {proposal.executed && <span className="executed">Executed</span>}
                  {Date.now() / 1000 > proposal.endTime && <span className="ended">Ended</span>}
                </div>
                {!proposal.executed && Date.now() / 1000 <= proposal.endTime && (
                  <div className="vote-buttons">
                    <button 
                      onClick={() => handleVote(proposal.id, true)}
                      className="vote-for"
                    >
                      Vote For
                    </button>
                    <button 
                      onClick={() => handleVote(proposal.id, false)}
                      className="vote-against"
                    >
                      Vote Against
                    </button>
                  </div>
                )}
              </div>
            ))}
            {proposals.length === 0 && (
              <p className="no-proposals">No active proposals</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default DAOInterface;
