import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './WalletCard.css';

function WalletCard({ account, balance, network }) {
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [nfts, setNfts] = useState([]);
  const [displayBalance, setDisplayBalance] = useState('0.00000');

  useEffect(() => {
    const updateBalance = async () => {
      try {
        const currentBalance = await account.provider.getBalance(account.address);
        setDisplayBalance(Number(ethers.utils.formatEther(currentBalance)).toFixed(5));
      } catch (error) {
        console.error('Error fetching balance:', error);
      }
    };

    if (account) {
      updateBalance();
      // Set up an interval to update the balance every 30 seconds
      const interval = setInterval(updateBalance, 30000);
      return () => clearInterval(interval);
    }
  }, [account]);

  useEffect(() => {
    const fetchNFTs = async () => {
      try {
        if (account) {
          // For development, let's return an empty array instead of making API calls
          setNfts([]);

          // Uncomment this when you have your API set up
          /*
          const response = await fetch(
            `https://api.opensea.io/api/v1/assets?owner=${account.address}&limit=20`
          );
          const data = await response.json();
          setNfts(data.assets || []);
          */
        }
      } catch (error) {
        console.error('Error fetching NFTs:', error);
        setNfts([]);
      }
    };

    fetchNFTs();
  }, [account]);

  const sendTransaction = async () => {
    try {
      const tx = await account.sendTransaction({
        to: recipient,
        value: ethers.utils.parseEther(amount)
      });
      await tx.wait();
      // Update balance after transaction
      const newBalance = await account.provider.getBalance(account.address);
      setDisplayBalance(Number(ethers.utils.formatEther(newBalance)).toFixed(5));
      alert('Transaction successful!');
      setRecipient('');
      setAmount('');
    } catch (error) {
      alert('Transaction failed: ' + error.message);
    }
  };

  return (
    <div className="wallet-card">
      <div className="balance-section">
        <h2>Balance</h2>
        <p className="balance">{displayBalance} ETH</p>
        <p className="network">{network}</p>
      </div>

      <div className="transfer-section">
        <h3>Send ETH</h3>
        <input
          type="text"
          placeholder="Recipient Address"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
        <input
          type="number"
          placeholder="Amount in ETH"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          step="0.00001"
        />
        <button onClick={sendTransaction}>Send</button>
      </div>

      <div className="nft-section">
        <h3>NFTs</h3>
        <div className="nft-grid">
          {nfts && nfts.length > 0 ? (
            nfts.map((nft) => (
              <div key={nft.id} className="nft-item">
                <img src={nft.image_url} alt={nft.name} />
                <p>{nft.name}</p>
              </div>
            ))
          ) : (
            <p>No NFTs found</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default WalletCard;
